import "./App.css";
import React, { useState, useEffect } from "react";
function App() {
  const storedItems = JSON.parse(localStorage.getItem("items"));
  const storedCompItems = JSON.parse(localStorage.getItem("completeditems"));
  const date = new Date();
  const time = date.toLocaleString("en-US", {
    hour: "numeric",
    minute: "numeric",
    hour12: true,
  });
  let currentDate = date.toJSON();

  const [userDesc, setUserDesc] = useState("");
  const [userInput, setUserInput] = useState("");
  const [items, setItems] = useState(storedItems);
  const [completeditems, setCompleteditems] = useState(storedCompItems);
  const [completedList, setCompletedList] = useState(false);

  function handleCompletedBar() {
    setCompletedList(true);
  }

  function handleToDodBar() {
    setCompletedList(false);
  }

  function handleChange(e) {
    e.preventDefault();
    setUserInput(e.target.value);
  }
  function handleChangeD(e) {
    e.preventDefault();
    setUserDesc(e.target.value);
  }
  const item = {
    id: Math.floor(Math.random() * 1000),
    value: userInput,
    discrption: userDesc,
    date: currentDate.slice(0, 10),
    time: time,
  };

  function addItem() {
    if (userInput.trim() === "") {
      return;
    }
    setItems((oldList) => [item, ...oldList]);
    setUserInput("");
    setUserDesc("");
  }

  function deleteItem(id) {
    const newArray = items.filter((item) => item.id !== id);
    const compItems = completeditems.filter((item) => item.id !== id);
    setItems(newArray);
    setCompleteditems(compItems);
  }

  function completeItem(id) {
    const compItem = items.filter((item) => item.id === id)[0];
    const deleteFromTodos = items.filter((item) => item.id !== id);
    setCompleteditems((preValue) => {
      return [compItem, ...preValue];
    });
    setItems(deleteFromTodos);
  }

  useEffect(() => {
    localStorage.setItem("items", JSON.stringify(items));
    localStorage.setItem("completeditems", JSON.stringify(completeditems));
  }, [items, completeditems]);

  return (
    <>
      <h1 className="header">ToDo App List</h1>
      <div className="todo-container">
        <div className="App">
          <div className="input-container">
            <form>
              <div className="inputs">
                <h3 className="input-title">Title:</h3>
                <input
                  type="text"
                  className="input input__lg"
                  placeholder="Add the Title of your To Do"
                  value={userInput}
                  onChange={handleChange}
                />
              </div>
              <div className="inputs">
                <h3 className="input-title">Description:</h3>
                <input
                  type="text"
                  className="input input__lg"
                  placeholder="Add the Description of your To Do"
                  value={userDesc}
                  onChange={handleChangeD}
                />
              </div>
              <div className="buttons-container">
                <button className="add-button" onClick={addItem}>
                  Add
                </button>
              </div>
            </form>
          </div>
          <div className="todo-bar">
            <div
              className="items-bar"
              style={{
                backgroundColor:
                  completedList === false ? "#06bf67" : " inherit",
              }}
              onClick={handleToDodBar}
            >
              <p className="bar-title">To Do</p>
            </div>
            <div
              className="completed-bar"
              style={{
                backgroundColor: completedList ? "#06bf67" : " inherit",
              }}
              onClick={handleCompletedBar}
            >
              <p className="bar-title">Completed</p>
            </div>
          </div>
          {completedList === false && (
            <div className="todo-items">
              {items.map((item) => {
                return (
                  <>
                    <div className="items-container">
                      <div className="item" key={item.id}>
                        <h2 className="todo-title">{item.value}</h2>
                        <p className="todo-description">{item.discrption}</p>
                      </div>
                      <div className="buttons-container">
                        <i
                          className="fa-solid fa-trash delete-button"
                          onClick={() => deleteItem(item.id)}
                        ></i>
                        <i
                          className="fa-solid fa-check complete-button"
                          onClick={() => completeItem(item.id)}
                        ></i>
                      </div>
                    </div>
                  </>
                );
              })}
            </div>
          )}
          {completedList && (
            <div className="todo-comp-items">
              {completeditems.map((item) => {
                return (
                  <>
                    <div className="items-container">
                      <div className="item" key={item.id}>
                        <h2 className="todo-title completed-item">
                          {item.value}
                        </h2>
                        <p className="todo-description">{item.discrption}</p>
                        <p className="date">
                          Conpleted on {item.date} at {item.time}
                        </p>
                      </div>
                      <div className="buttons-container">
                        <i
                          className="fa-solid fa-trash delete-button"
                          onClick={() => deleteItem(item.id)}
                        ></i>
                      </div>
                    </div>
                  </>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default App;
